import { useQuery, useMutation } from "@apollo/client";
import { useEffect, useState } from "react";
import { LOAD_SUPPLIERS, GET_SUPPLIER } from "../gql/Queries";
import {  } from "../gql/Queries";
import { ADD_FLEET } from "../gql/Mutations";

const AddFleet = () => {
  const { error, loading, data } = useQuery(LOAD_SUPPLIERS);
  const [suppliers, setSuppliers] = useState();
  const [name, setName] = useState();
  const [supplierId, setSupplierId] = useState();

  const [addFleet] = useMutation(ADD_FLEET);

  const handleSubmit = (e) => {
    e.preventDefault();
    addFleet({
      variables: {
        name: name,
        supplierId: supplierId,
      },
      refetchQueries: [{ query: GET_SUPPLIER, variables: { id: supplierId } }],
    });
  };

  useEffect(() => {
    if (data !== undefined) {
      setSuppliers(data.suppliers);
    }
  }, [data]);
  return (
    <div>
      {(loading && <p>Loading...</p>) || (
        <form id="add-fleet" onSubmit={handleSubmit}>
          <div className="field">
            <label>Fleet Owner Name:</label>
            <input
              type="text"
              value={name || ""}
              onChange={(e) => setName(e.target.value)}
            ></input>
          </div>

          <div className="field">
            <label>Supplier:</label>
            <select
              onChange={(e) => {
                setSupplierId(e.target.value);
              }}
            >
              <option>Select Supplier</option>
              {suppliers != undefined &&
                suppliers.map((supplier, idx) => (
                  <option key={idx} value={supplier.id}>
                    {supplier.name}
                  </option>
                ))}
            </select>
          </div>

          <button type="submit">Add Fleet Owner</button>
        </form>
      )}
    </div>
  );
};

export default AddFleet;
