  //       <p>
  //         {currency}: {rate}
  //       </p>
  //     </div>
  //   ));

  console.log(data);
}

const BookList = () => {
  return (
    <div>
      <ul id="book-list">
        <li>Book name</li>
        <button onClick={() => ExchangeRates()}></button>
      </ul>
    </div>
  );
};

export default BookList;
