import { gql } from "@apollo/client";

export const ADD_BOOK = gql`
  mutation addBook($name: String!, $genre: String!, $authorId: String!) {
    addBook(name: $name, genre: $genre, authorId: "") {
      name
      id
    }
  }
`;
