import { useQuery, useMutation } from "@apollo/client";
import { useEffect, useState } from "react";
import { LOAD_FLEETS, LOAD_DRIVERS, GET_SUPPLIER } from "../gql/Queries";
import { ADD_VEHICLE } from "../gql/Mutations";

const AddVehicle = () => {
  const [fleets, setFleets] = useState();
  const [drivers, setDrivers] = useState();
  const [name, setName] = useState();
  const [fleetId, setFleetId] = useState();
  const [driverId, setDriverId] = useState();
  const [capacity, setCapacity] = useState();
  const [price, setPrice] = useState();
  const [available, setAvailable] = useState();
  const {
    error: errorF,
    loading: loadingF,
    data: dataF,
  } = useQuery(LOAD_FLEETS);
  const {
    error: errorD,
    loading: loadingD,
    data: dataD,
  } = useQuery(LOAD_DRIVERS);
  // const [addFleet] = useMutation(ADD_FLEET);

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   addFleet({
  //     variables: {
  //       name: name,
  //       supplierId: supplierId,
  //     },
  //     refetchQueries: [{ query: GET_SUPPLIER, variables: { id: supplierId } }],
  //   });
  // };

  useEffect(() => {
    if (dataF !== undefined) {
      console.log(dataF);
      setFleets(dataF.fleets);
    }

    if (dataD !== undefined) {
      console.log(dataD);
      setDrivers(dataD.drivers);
    }
  }, [dataF, dataD]);
  return (
    <div>
      <h3>Add Vehicle</h3>
      {(loadingF && loadingD && <p>Loading...</p>) || (
        <form id="add-fleet" onSubmit={handleSubmit}>
          <div className="field">
            <label>Vehicle Name:</label>
            <input
              type="text"
              value={name || ""}
              onChange={(e) => setName(e.target.value)}
            ></input>
          </div>

          <div className="field">
            <label>Capacity:</label>
            <input
              type="text"
              value={capacity || ""}
              onChange={(e) => setCapacity(e.target.value)}
            ></input>
          </div>

          <div className="field">
            <label>Price:</label>
            <input
              type="text"
              value={capacity || ""}
              onChange={(e) => setCapacity(e.target.value)}
            ></input>
          </div>

          <div className="field">
            <label>Supplier:</label>
            <select
              onChange={(e) => {
                setSupplierId(e.target.value);
              }}
            >
              <option>Select Supplier</option>
              {suppliers != undefined &&
                suppliers.map((supplier, idx) => (
                  <option key={idx} value={supplier.id}>
                    {supplier.name}
                  </option>
                ))}
            </select>
          </div>

          <button type="submit">Add Fleet Owner</button>
        </form>
      )}
    </div>
  );
};

export default AddVehicle;
