import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { GET_SUPPLIER, LOAD_DRIVERS } from "../gql/Queries";
import { UPDATE_VEHICLE } from "../gql/Mutations";

const SupplierDetails = (props) => {
  const [supplier, setSupplier] = useState();
  const [drivers, setDrivers] = useState();
  const [driverId, setDriverId] = useState();
  const [selected, setSelected] = useState();
  const [book, setBook] = useState(false);
  const { data } = useQuery(GET_SUPPLIER, {
    variables: {
      id: props.supplierId,
    },
  });

  const { loading: loadingD, data: dataD } = useQuery(LOAD_DRIVERS);
  useEffect(() => {
    if (data !== undefined) {
      console.log(data.supplier);
      setSupplier(data.supplier);
    }
    if (dataD !== undefined) {
      console.log(dataD);
      setDrivers(dataD.drivers);
    }
  }, [data, dataD]);

  const handleOrder = () =>{
    
  }
 
  return (
    <div id="supplier-details">
      {supplier !== undefined && (
        <div>
          <ul>
            <h1>Fleets: </h1>
            {supplier.fleets.map((fleet, idx) => (
              <li key={idx}>
                <h2 onClick={() => setSelected(fleet.name)}>{fleet.name}</h2>
                {selected === fleet.name && (
                  <div className="vehicles">
                    <h1>Vehicles</h1>
                    <ol>
                      {fleet.vehicles.map((vehicle, idy) => (
                        <div>
                          {(vehicle.available == "true" && (
                            <div>
                              <li key={idy}>
                                <div>
                                  <p>
                                    Name:
                                    <span> {vehicle.name}</span>
                                  </p>
                                  <p>
                                    Capacity:
                                    <span> {vehicle.capacity}</span>
                                  </p>
                                  <p>
                                    Price:
                                    <span> {vehicle.price}</span>
                                  </p>
                                  <p>
                                    Driver:
                                    <span> {vehicle.driver.name}</span>
                                  </p>
                                  <p>
                                    Available:
                                    <span> {vehicle.available}</span>
                                  </p>
                                </div>

                                <div
                                  id="radio"
                                  onChange={(e) => setBook(e.target.value)}
                                >
                                  <input
                                    type="radio"
                                    value={true}
                                    name="book"
                                  />
                                  <p>Order</p>
                                </div>
                                {book && (
                                  <div className="field">
                                    <label>Driver:</label>
                                    <select
                                      onChange={(e) => {
                                        setDriverId(e.target.value);
                                      }}
                                    >
                                      <option>Select Driver</option>
                                      {drivers !== undefined &&
                                        drivers.map((driver, idx) => (
                                          <option key={idx} value={driver.id}>
                                            {driver.name}
                                          </option>
                                        ))}
                                    </select>
                                    <button onClick={handleOrder}>Order</button>
                                  </div>
                                )}
                              </li>
                            </div>
                          )) || <h3>No vehicles available</h3>}
                        </div>
                      ))}
                    </ol>
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SupplierDetails;
