import "./App.css";
import SuppliersList from "./components/SuppliersList";
import AddSupplier from "./components/AddSupplier";
import AddVehicle from "./components/AddVehicle";
import AddDriver from "./components/AddDriver";
import AddFleet from "./components/AddFleet";
import DeleteSupplier from "./components/DeleteSupplier";
import DeleteFleet from "./components/DeleteFleet";
import { atom, useRecoilState, useRecoilValue } from "recoil";

import { ApolloClient, InMemoryCache, ApolloProvider } from "@apollo/client";

const client = new ApolloClient({
  uri: "http://localhost:4000/graphql",
  cache: new InMemoryCache(),
});



function App() {
  // const { error, loading, data } = useQuery(LOAD_BOOKS);
  const clicked = useRecoilValue(clickedAtom);
  return (
    <ApolloProvider client={client}>
      <div className="App">
        <SuppliersList />
        <AddSupplier />
        <DeleteSupplier />
        <DeleteFleet />
        <AddFleet />
        <AddVehicle />
        <AddDriver />
      </div>
    </ApolloProvider>
  );
}

export default App;
