const BookList = () => {
  const getBooksQuery = gql`
    {
      books {
        name
      }
    }
  `;

  const { loading, error, data } = useQuery(getBooksQuery);

  console.log(data);
  return (
    <div>
      <ul id="book-list">
        <li>Book name</li>
        {loading && <p>Loading...</p>}
        {error && <p>Error</p>}
        {/* <button onClick={() => ExchangeRates()}>Get Data</button> */}
      </ul>
    </div>
  );
};

export default BookList;
