import { gql } from "@apollo/client";

export const LOAD_SUPPLIERS = gql`
  query {
    suppliers {
      id
      name
    }
  }
`;

export const LOAD_FLEETS = gql`
  query {
    fleets {
      name
      id
    }
  }
`;

export const LOAD_DRIVERS = gql`
  query {
    drivers {
      name
      id
      available
    }
  }
`;
 
export const GET_SUPPLIER = gql`
  query ($id: ID) {
    supplier(id: $id) {
      id
      name
      fleets {
        name
        vehicles {
          name
          capacity
          price
          driver {
            name
          }
        }
      }
    }
  }
`;
