import { useQuery, useMutation } from "@apollo/client";
import { PromiseProvider } from "mongoose";
import { useEffect, useState } from "react";
import { GET_SUPPLIER } from "../gql/Queries";
import AddFleet from "./AddFleet";

const SupplierDetails = (props) => {
  const [supplier, setSupplier] = useState();
  const { error, loading, data } = useQuery(GET_SUPPLIER, {
    variables: {
      id: props.supplierId,
    },
  });
  useEffect(() => {
    if (data !== undefined) {
      console.log(data.supplier);
      setSupplier(data.supplier);
    }
  }, [data]);
  return (
    <div id="supplier-details">
      {supplier != undefined && (
        <div>
          <ul>
            <h2>Fleets: </h2>
            {supplier.fleets.map((fleet, idx) => (
              <li key={idx}>
                <h2 oncl>{fleet.name}</h2>
                {/* <h3>Vehicles</h3>
                <ol>
                  {fleet.vehicles.map((vehicle, idy) => (
                    <li key={idy}>
                      <p>Name: {vehicle.name}</p>
                      <p>Capacity: {vehicle.capacity}</p>
                      <p>Price: {vehicle.price}</p>
                      <p>Driver: {vehicle.driver.name}</p>
                    </li>
                  ))}
                </ol> */}
              </li>
            ))}
          </ul>
          {supplier != undefined && <AddFleet />}
          {/* {supplier != undefined && <AddVehicle />} */}
          {/* <AddVehicle /> */}
        </div>
      )}
    </div>
  );
};

export default SupplierDetails;
