import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { LOAD_AUTHORS } from "../gql/Queries";

const AddBook = () => {
  const { error, loading, data } = useQuery(LOAD_AUTHORS);

  const [authors, setAuthors] = useState();
  const [bookname, setBookname] = useState();
  const [genre, setGenre] = useState();
  useEffect(() => {
    if (data !== undefined) {
      setAuthors(data.authors);
    }
  }, [data]);
  return (
    <div>
      {(loading && <p>Loading...</p>) || (
        <form id="add-book">
          <div className="field">
            <label>Book name:</label>
            <input type="text" value={bookname} onChange={(e)=>{}}/>
          </div>

          <div className="field">
            <label>Genre:</label>
            <input type="text" />
          </div>

          <div className="field">
            <label>Author:</label>
            <select>
              <option disabled>Select Author</option>
              {authors != undefined &&
                authors.map((author, idx) => (
                  <option key={idx} value={author.id}>
                    {author.name}
                  </option>
                ))}
            </select>
          </div>

          <button>+</button>
        </form>
      )}
    </div>
  );
};

export default AddBook;
