import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { LOAD_SUPPLIERS } from "../gql/Queries";
import SupplierDetails from "./SupplierDetails";
import { atom, useRecoilState, useRecoilValue } from "recoil";

const darkModeAtom = atom({
  key: "selected",
  default: false,
});

const SuppliersList = () => {
  const { error, loading, data } = useQuery(LOAD_SUPPLIERS);
  const [suppliers, setSuppliers] = useState();
  const [selected, setSelected] = useState("null");

  useEffect(() => {
    if (data !== undefined) {
      setSuppliers(data.suppliers);
      console.log(data.suppliers);
    }
  }, [data]);
  return (
    <div>
      {loading && <p>Loading...</p>}
      {error && <p>An error occured...</p>}

      {suppliers != undefined && (
        <ul>
          {suppliers.map((supplier, idx) => (
            <li key={idx} onClick={(e) => setSelected(supplier.id)}>
              <h1>{supplier.name}</h1>
              {selected === supplier.id && (
                <SupplierDetails supplierId={selected} />
              )}
              {/* {selected === supplier.id && <AddVehicle supplierId={selected} />}
              {selected === supplier.id && <AddDriver supplierId={selected} />} */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SuppliersList;
