import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { LOAD_AUTHORS } from "../gql/Queries";

const AddBook = () => {
  const { error, loading, data } = useQuery(LOAD_AUTHORS);
  const [authors, setAuthors] = useState();
  useEffect(() => {
    if (data !== undefined) {
      console.log(data.authors);
    }
  }, [data]);
  return (
    <div>
        {loading && <p>Loading...</p>}
      <form id="add-book">
        <div className="field">
          <label>Book name:</label>
          <input type="text" />
        </div>

        <div className="field">
          <label>Genre:</label>
          <input type="text" />
        </div>

        <div className="field">
          <label>Author:</label>
          <select>
            <option>Select Author</option>
          </select>
        </div>

        <button>+</button>
      </form>
    </div>
  );
};

export default AddBook;
