import { gql } from "@apollo/client";

export const LOAD_BOOKS = gql`
  query {
    books {
      name
      genre
    }
  }
`;

export const LOAD_AUTHORS = gql`
  query {
    authors {
      name
      id
    }
  }
`;

export const GET_BOOK = gql`
  query($id: String) {
    book(id: $id){
      id
      name
      genre
      author{
        id
        name
        age
        books{
          
        }
      }
    }
  }
`;
