import { gql } from "@apollo/client";

export const LOAD_BOOKS = gql`
  query {
    books {
      id
      name
      genre
    }
  }
`;

export const LOAD_SUPPLIERS = gql`
  query {
    suppliers {
      id
      name
    }
  }
`;

export const LOAD_AUTHORS = gql`
  query {
    authors {
      name
      id
    }
  }
`;

export const GET_BOOK = gql`
  query ($id: ID) {
    book(id: $id) {
      id
      name
      genre
      author {
        id
        name
        age
        books {
          name
          id
        }
      }
    }
  }
`;

export const GET_SUPPLIER = gql`
  query ($id: ID) {
    supplier(id: $id) {
      id
      name
     
  }
`;
