import "./App.css";
import BookList from "./components/BookList";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";
import { LOAD_BOOKS } from "./gql/";

const client = new ApolloClient({
  uri: "http://localhost:4000/graphql",
  cache: new InMemoryCache(),
});

function App() {
  const { error, loading, data } = useQuery(LOAD_BOOKS);
  return (
    <ApolloProvider client={client}>
      {/* <div className="App">
        <h1>Reading List</h1> */}
      <BookList />
      {/* </div> */}
    </ApolloProvider>
  );
}

export default App;
